import styled from 'styled-components'
export const Button = styled.button`
  display: flex;
  color: #fff;
  height: 3rem;
  border: none;
  border-radius: 2px;
  align-items: center;
  justify-content: center;
  padding: 0.1rem 0.6rem;
  background-color: #ee4d2d;
  transition: 0.2s background ease;
  &:hover {
    background: #f05d40;
  }
`
