import React from 'react'
import { Link } from 'react-router-dom'
import { useForm, Controller } from 'react-hook-form'
import * as S from './register.style'
import { Button } from 'src/assets/styles/utilities'
import { path } from 'src/constants/path'
import InputText from 'src/components/InputText/InputText'
import InputPassword from 'src/components/InputPassword/InputPassword'
import { rules } from 'src/constants/rules'

export default function Register() {
  const {
    control,
    formState: { errors },
    handleSubmit,
    getValues
  } = useForm({
    defaultValues: {
      email: '',
      password: '',
      confirmedPassword: ''
    }
  })

  const handleRegister = data => {
    console.log(data)
  }

  console.log(errors)

  return (
    <S.Container className="container">
      <S.Left />
      <S.FormWrapper>
        <S.FormTitle>đăng ký</S.FormTitle>
        <S.Form novalidate onSubmit={handleSubmit(handleRegister)}>
          <S.FormControl>
            <Controller
              name="email"
              control={control}
              rules={rules.email}
              render={({ field }) => (
                <InputText
                  type="text"
                  placeholder="Email"
                  name="email"
                  onChange={field.onChange}
                  value={getValues('email')}
                />
              )}
            />
          </S.FormControl>
          <S.FormControl>
            <Controller
              name="password"
              control={control}
              rules={rules.password}
              render={({ field }) => (
                <InputPassword
                  placeholder="Mật khẩu"
                  name="password"
                  onChange={field.onChange}
                  value={getValues('password')}
                />
              )}
            />
          </S.FormControl>
          <S.FormControl>
            <Controller
              name="confirmedPassword"
              control={control}
              rules={{
                ...rules.confirmedPassword,
                validate: {
                  samePassword: v =>
                    v === getValues('password') || 'Mật khẩu không khớp.'
                }
              }}
              render={({ field }) => (
                <InputPassword
                  placeholder="Nhập lại mật khẩu"
                  name="confirmedPassword"
                  onChange={field.onChange}
                  value={getValues('confirmedPassword')}
                />
              )}
            />
          </S.FormControl>
          <S.FormButton>
            <Button type="submit">Đăng ký</Button>
          </S.FormButton>
        </S.Form>
        <S.FormFooter>
          <span>Bạn đã có tài khoản?</span>
          <Link to={path.login} className="link">
            Đăng nhập
          </Link>
        </S.FormFooter>
      </S.FormWrapper>
    </S.Container>
  )
}
