  justify-content: space-between;
`
export const MoreProduct = styled.p`
  font-size: 1.2rem;
  cursor: default;
`
export const CartButton = styled(Link)`
  padding: 1rem 2rem;
  color: #fff;
  border-radius: 3px;
  background: #ee4d2d;
`
