import React from 'react'
import { Link, useLocation, useNavigate } from 'react-router-dom'
import { path } from 'src/constants/path'
import RatingStars from '../RatingStars/RatingStars'
import * as S from './filterPanel.style'
import PropTypes from 'prop-types'
import qs from 'query-string'
import useQuery from 'src/hooks/useQuery'
import { Controller, useForm } from 'react-hook-form'

export default function FilterPanel({ categories, filters }) {
  const location = useLocation()
  const navigate = useNavigate()
  const query = useQuery()
  const {
    control,
    getValues,
    handleSubmit,
    formState: { errors },
    clearErrors,
    reset,
    setValue
  } = useForm({
    defaultValues: {
      minPrice: filters.minPrice || '',
      maxPrice: filters.maxPrice || ''
    }
  })

  const validPrice = () => {
    const maxPrice = getValues('maxPrice')
    const minPrice = getValues('minPrice')
    const message = 'Vui lòng điền khoảng giá phù hợp'
    if (minPrice !== '' && maxPrice !== '') {
      return maxPrice > minPrice || message
    }
    return minPrice !== '' || maxPrice !== '' || message
  }

  const handleActiveClassItem = category => {
    const query = qs.parse(location.search)
    return query.category === category._id ? 'active' : ''
  }
  return (
    <div>
      <S.CategoryTitleLink to={path.home}>
        <i className="fas fa-bars"></i>
        tất cả danh mục
      </S.CategoryTitleLink>
      <S.CategoryList>
        {categories.map(category => {
          return (
            <S.CategoryItem key={category._id}>
              <Link
                to={path.home + `?category=${category._id}`}
                className={handleActiveClassItem(category)}
              >
                {category.name}
              </Link>
            </S.CategoryItem>
          )
        })}
      </S.CategoryList>
      <S.CategoryTitle>
        <i className="fas fa-filter"></i>
        bộ lọc tìm kiếm
      </S.CategoryTitle>
      <S.FilterGroup>
        <S.FilterGroupHeader>khoảng giá</S.FilterGroupHeader>
        <S.PriceRange>
          <S.PriceRangeGroup>
            <Controller
              name="minPrice"
              control={control}
              rules={{
                validate: { validPrice }
              }}
              render={({ field }) => (
                <S.PriceRangeInput
                  placeholder="đ TỪ"
                  onChange={field.onChange}
                  value={getValues('minPrice')}
                ></S.PriceRangeInput>
              )}
            />
            <Controller
              name="minPrice"
              control={control}
              rules={{
                validate: { validPrice }
              }}
              render={({ field }) => (
                <S.PriceRangeInput
                  placeholder="đ TỪ"
                  onChange={field.onChange}
                  value={getValues('minPrice')}
                ></S.PriceRangeInput>
              )}
            />
            <S.PriceRangeLine />
            <S.PriceRangeInput placeholder="đ ĐẾN"></S.PriceRangeInput>
          </S.PriceRangeGroup>
          <S.PriceErrorMessage>
            Vui lòng điền khoảng giá phù hợp
          </S.PriceErrorMessage>
          <S.PriceRangeButton>Áp dụng</S.PriceRangeButton>
        </S.PriceRange>
      </S.FilterGroup>

      <S.FilterGroup>
        <S.FilterGroupHeader>đánh giá</S.FilterGroupHeader>
        <RatingStars />
      </S.FilterGroup>

      <S.RemoveFilterButton>Xóa tất cả</S.RemoveFilterButton>
    </div>
  )
}

FilterPanel.propTypes = {
  categories: PropTypes.array.isRequired,
  filters: PropTypes.object.isRequired
}
