import styled from 'styled-components'

export const ProductBrief = styled.div`
  margin-top: 1.5rem;
  display: flex;
  background-color: #fff;
`
export const ProductImage = styled.div`
  width: 480px;
  padding: 1.5rem;
`
export const ProductMainImage = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  img {
    max-width: 100%;
    max-height: 100%;
  }
`
export const ProductSlide = styled.div`
  position: relative;
  display: flex;
  overflow: hidden;
  flex-direction: row;
  max-width: 100%;
  max-height: 90px;
  img {
    padding: 2px;
    max-width: 8.8rem;
    max-height: 8.8rem;
  }
`

export const ButtonIcon = styled.button`
  position: absolute;
  border: none;
  align-items: center;
  justify-content: center;
  background-color: rgba(0, 0, 0, 0.2);
`
export const ProductButtonIconPrev = styled(ButtonIcon)`
  /* top: 50%; */
  left: 0;
  width: 2rem;
  height: 4rem;
  justify-content: center;
  align-items: center;
`
export const ProductButtonIconNext = styled(ButtonIcon)``
export const ProductImageItem = styled.div``

