export const Drawer = styled.div`
  position: absolute;
  left: 0;
  z-index: 1;
  top: 100%;
  background: #fff;
  border-radius: 5px;
  animation: animationPopOver 0.2s cubic-bezier(0.4, 0, 0.6, 1);
`
export const PopoverArrow = styled.div`
  position: inherit;
  width: 40px;
  z-index: -1;
  height: 40px;
  margin-left: 40%;
  transform: rotate(45deg);
  background-color: #fff;
`
export const PopoverContent = styled.div`
  display: flex;
  flex-direction: column;
  transition: ease-in-out 0.2s;
`

